import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Axios from 'axios';

function UpdateProfile() {
  const { id } = useParams();
  const [formData, setFormData] = useState({
    fullname: '',
    email: '',
    password: '',
    phone: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await Axios.get(`http://localhost:8080/user/${id}`);
        const itemData = response.data;
        setFormData({
          fullname: itemData.fullname || '',
          email: itemData.email || '',
          password: itemData.password || '',
          phone: itemData.phone || '',
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, [id]);

  const onInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await Axios.put(`http://localhost:8080/user/${id}`, formData);
      alert('User updated successfully');
      navigate('/UserProfile');
    } catch (error) {
      alert('Failed to update user');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={onSubmit} className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md border border-gray-200">
        <h1 className="text-2xl font-semibold text-center text-gray-800 mb-6">Update Profile</h1>

        <label htmlFor="fullname" className="block font-medium text-gray-700">Full Name</label>
        <input
          type="text"
          id="fullname"
          name="fullname"
          onChange={onInputChange}
          value={formData.fullname}
          required
          className="w-full p-2 border border-gray-300 rounded mt-1 mb-4"
        />

        <label htmlFor="email" className="block font-medium text-gray-700">Email</label>
        <input
          type="email"
          id="email"
          name="email"
          onChange={onInputChange}
          value={formData.email}
          required
          className="w-full p-2 border border-gray-300 rounded mt-1 mb-4"
        />

        <label htmlFor="password" className="block font-medium text-gray-700">Password</label>
        <input
          type="password"
          id="password"
          name="password"
          onChange={onInputChange}
          value={formData.password}
          required
          className="w-full p-2 border border-gray-300 rounded mt-1 mb-4"
        />

        <label htmlFor="phone" className="block font-medium text-gray-700">Phone</label>
        <input
          type="tel"
          id="phone"
          name="phone"
          onChange={onInputChange}
          value={formData.phone}
          required
          className="w-full p-2 border border-gray-300 rounded mt-1 mb-6"
        />

        <button
          type="submit"
          className="w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded font-medium transition"
        >
          Update
        </button>
      </form>
    </div>
  );
}

export default UpdateProfile;
