import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      setError('No user ID found. Please log in.');
      setLoading(false);
      return;
    }

    Axios.get(`http://localhost:8080/user/${userId}`)
      .then((response) => {
        setUser(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to fetch user data');
        setLoading(false);
      });
  }, []);

  const updateNavigate = (id) => {
    window.location.href = `/UpdateProfile/${id}`;
  };

  const deleteAccount = async () => {
    const confirmDelete = window.confirm(
      'Are you sure you want to delete your account?'
    );
    if (confirmDelete && user?.id) {
      try {
        await Axios.delete(`http://localhost:8080/user/${user.id}`);
        alert('Account deleted successfully');
        localStorage.removeItem('userId');
        window.location.href = '/Register';
      } catch (error) {
        console.error('Error deleting account:', error);
        alert('Failed to delete account');
      }
    }
  };

  if (loading) return <p className="text-gray-600 text-center mt-10">Loading...</p>;
  if (error) return <p className="text-red-500 text-center mt-10 font-medium">{error}</p>;

  return (
    <div className="p-4 max-w-md mx-auto bg-white rounded-md shadow border border-gray-200 mt-10">
      <h2 className="text-xl font-semibold text-gray-800 mb-3 text-center">User Profile</h2>
      {user ? (
        <div className="space-y-2 text-gray-700 text-sm">
          <div className="flex justify-between">
            <span className="font-medium text-gray-600">Full Name:</span>
            <span>{user.fullname}</span>
          </div>
          <div className="flex justify-between">
            <span className="font-medium text-gray-600">Email:</span>
            <span>{user.email}</span>
          </div>
          <div className="flex justify-between">
            <span className="font-medium text-gray-600">Phone:</span>
            <span>{user.phone}</span>
          </div>
          <div className="flex justify-between">
            <span className="font-medium text-gray-600">Password:</span>
            <span>{user.password}</span>
          </div>

          <div className="flex justify-center gap-3 pt-4">
            <button
              onClick={() => updateNavigate(user.id)}
              className="px-4 py-1.5 bg-blue-600 hover:bg-blue-700 text-white text-sm rounded shadow"
            >
              Update
            </button>
            <button
              onClick={deleteAccount}
              className="px-4 py-1.5 bg-red-600 hover:bg-red-700 text-white text-sm rounded shadow"
            >
              Delete
            </button>
          </div>
        </div>
      ) : (
        <p className="text-center text-gray-500">No user data available.</p>
      )}
    </div>
  );
}

export default UserProfile;
