import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./UpdateItem.css";

function UpdateItem() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [itemData, setItemData] = useState({
    itemId: "",
    itemName: "",
    itemCategory: "",
    itemQty: "",
    itemDetails: "",
    itemImage: ""
  });
  const [previewImage, setPreviewImage] = useState(null);
  const [file, setFile] = useState(null);

  // Fetch item data on component mount
  useEffect(() => {
    const fetchItemData = async () => {
      setIsLoading(true);
      setError(null);
      
      try {
        console.log(`Fetching item data for ID: ${id}`);
        const response = await axios.get(`http://localhost:8080/inventory/${id}`, {
          timeout: 10000, // 10 second timeout
          headers: {
            'Content-Type': 'application/json',
          }
        });
        
        console.log('Item data received:', response.data);
        setItemData(response.data);
        
        // Set preview image if it exists
        if (response.data.itemImage) {
          setPreviewImage(`http://localhost:8080/uploads/${response.data.itemImage}`);
        }
      } catch (err) {
        console.error("Error fetching item data:", err);
        
        let errorMessage = "Failed to load item data. ";
        if (err.response) {
          // Server responded with error status
          errorMessage += `Server error: ${err.response.status} - ${err.response.data?.message || err.response.statusText}`;
        } else if (err.request) {
          // Request was made but no response received
          errorMessage += "No response from server. Please check if the server is running.";
        } else {
          // Something else happened
          errorMessage += err.message;
        }
        
        setError(errorMessage);
      } finally {
        setIsLoading(false);
      }
    };

    if (id) {
      fetchItemData();
    } else {
      setError("No item ID provided");
      setIsLoading(false);
    }
  }, [id]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setItemData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  // Handle image file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      // Validate file type
      if (!selectedFile.type.startsWith('image/')) {
        setError("Please select a valid image file");
        return;
      }
      
      // Validate file size (5MB limit)
      if (selectedFile.size > 5 * 1024 * 1024) {
        setError("Image file size must be less than 5MB");
        return;
      }
      
      setFile(selectedFile);
      
      // Create a preview of the selected image
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  // Validate form data
  const validateForm = () => {
    if (!itemData.itemName.trim()) {
      setError("Item name is required");
      return false;
    }
    if (!itemData.itemCategory.trim()) {
      setError("Category is required");
      return false;
    }
    if (!itemData.itemQty || itemData.itemQty < 0) {
      setError("Quantity must be a positive number");
      return false;
    }
    return true;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setIsLoading(true);
    setError(null);

    try {
      console.log('Submitting update for item:', itemData);
      
      // Try different approaches based on whether we have a file upload
      let response;
      
      if (file) {
        // If we have a new file, use FormData
        const formData = new FormData();
        formData.append("itemId", itemData.itemId);
        formData.append("itemName", itemData.itemName.trim());
        formData.append("itemCategory", itemData.itemCategory.trim());
        formData.append("itemQty", itemData.itemQty.toString());
        formData.append("itemDetails", itemData.itemDetails.trim());
        formData.append("itemImage", file);

        console.log('Sending FormData with new image file:', file.name);
        
        response = await axios.put(`http://localhost:8080/inventory/${id}`, formData, {
          headers: {
            "Content-Type": "multipart/form-data"
          },
          timeout: 30000
        });
      } else {
        // If no new file, send JSON data
        const updateData = {
          itemId: itemData.itemId,
          itemName: itemData.itemName.trim(),
          itemCategory: itemData.itemCategory.trim(),
          itemQty: parseInt(itemData.itemQty),
          itemDetails: itemData.itemDetails.trim(),
          itemImage: itemData.itemImage // Keep existing image
        };

        console.log('Sending JSON data (no new image):', updateData);
        
        response = await axios.put(`http://localhost:8080/inventory/${id}`, updateData, {
          headers: {
            "Content-Type": "application/json"
          },
          timeout: 10000
        });
      }

      console.log('Update successful:', response.data);
      
      // Navigate back to inventory list on success
      navigate("/allitems", { 
        state: { message: 'Item updated successfully!' }
      });
      
    } catch (err) {
      // console.error("Error updating item:", err);
      // console.error("Error details:", {
      //   message: err.message,
      //   response: err.response?.data,
      //   status: err.response?.status,
      //   config: err.config
      // });
      
      let errorMessage = "Failed to update item. ";
      if (err.response) {
        const status = err.response.status;
        const data = err.response.data;
        
        switch (status) {
          case 400:
            errorMessage += `Bad Request - ${data?.message || 'Invalid data format or missing required fields'}`;
            break;
          case 404:
            errorMessage += `Item not found - The item with ID ${id} doesn't exist`;
            break;
          case 500:
            errorMessage += `Server Error - ${data?.message || 'Internal server error'}`;
            break;
          default:
            errorMessage += `Server error: ${status} - ${data?.message || err.response.statusText}`;
        }
      } else if (err.request) {
        errorMessage += "No response from server. Please check if the server is running on localhost:8080";
      } else {
        errorMessage += err.message;
      }
      
      setError(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  // Navigate back to inventory list
  const handleCancel = () => {
    navigate("/allitems");
  };

  if (isLoading && !itemData.itemId) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading item data...</p>
      </div>
    );
  }

  return (
    <div className="update-item-container">
      <h1>Update Item {id ? `(ID: ${id})` : ''}</h1>
      
      {error && (
        <div className="error-message">
          <strong>Error:</strong> {error}
          <button onClick={() => setError(null)} className="dismiss-btn">
            Dismiss
          </button>
        </div>
      )}

      <form onSubmit={handleSubmit} className="update-form">
        <div className="form-group">
          <label htmlFor="itemId">Item ID:</label>
          <input
            type="text"
            id="itemId"
            name="itemId"
            value={itemData.itemId}
            onChange={handleInputChange}
            required
            readOnly
            className="form-control readonly"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemName">Item Name: *</label>
          <input
            type="text"
            id="itemName"
            name="itemName"
            value={itemData.itemName}
            onChange={handleInputChange}
            required
            className="form-control"
            placeholder="Enter item name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemCategory">Category: *</label>
          <input
            type="text"
            id="itemCategory"
            name="itemCategory"
            value={itemData.itemCategory}
            onChange={handleInputChange}
            required
            className="form-control"
            placeholder="Enter category"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemQty">Quantity: *</label>
          <input
            type="number"
            id="itemQty"
            name="itemQty"
            value={itemData.itemQty}
            onChange={handleInputChange}
            required
            min="0"
            className="form-control"
            placeholder="Enter quantity"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemDetails">Details:</label>
          <textarea
            id="itemDetails"
            name="itemDetails"
            value={itemData.itemDetails}
            onChange={handleInputChange}
            rows="4"
            className="form-control"
            placeholder="Enter item details (optional)"
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="itemImage">Image:</label>
          <input
            type="file"
            id="itemImage"
            name="itemImage"
            onChange={handleFileChange}
            accept="image/*"
            className="form-control"
          />
          <small className="form-text">
            Supported formats: JPG, PNG, GIF. Max size: 5MB
          </small>
          {previewImage && (
            <div className="image-preview">
              <img
                src={previewImage}
                alt="Item preview"
                className="preview-img"
                onError={(e) => {
                  console.error('Image load error:', e);
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/150?text=No+Image';
                }}
              />
              <p className="image-caption">
                {file ? 'New image selected' : 'Current image'}
              </p>
            </div>
          )}
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={handleCancel}
            className="btn cancel-btn"
            disabled={isLoading}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="btn update-btn"
            disabled={isLoading}
          >
            {isLoading ? "Updating..." : "Update Item"}
          </button>
        </div>
      </form>
      
      {/* Debug info - remove in production */}
      {process.env.NODE_ENV === 'development' && (
        <div className="debug-info" style={{marginTop: '20px', padding: '10px', backgroundColor: '#f5f5f5', fontSize: '12px'}}>
          <h4>Debug Info:</h4>
          <p><strong>Item ID from URL:</strong> {id}</p>
          <p><strong>Current Item Data:</strong> {JSON.stringify(itemData, null, 2)}</p>
          <p><strong>Has New File:</strong> {file ? 'Yes' : 'No'}</p>
        </div>
      )}
    </div>
  );
}

export default UpdateItem;
