import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userId = localStorage.getItem('userId');
        if (!userId) {
          setError('No user ID found. Please log in.');
          setLoading(false);
          return;
        }

        const response = await Axios.get(`http://localhost:8080/user/${userId}`);
        setUser(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setError('Failed to fetch user data');
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  const handleUpdateClick = (id) => {
    navigate(`/UpdateProfile/${id}`);
  };

  const handleLogout = () => {
    localStorage.removeItem('userId');
    navigate('/login'); // Adjust path as needed
  };

  if (loading) {
    return (
      <div className="p-6 max-w-md mx-auto">
        <p className="text-center">Loading...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 max-w-md mx-auto">
        <p className="text-red-600 text-center">{error}</p>
        <button 
          onClick={() => navigate('/login')} 
          className="mt-4 w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
        >
          Go to Login
        </button>
      </div>
    );
  }

  return (
    <div className="p-6 max-w-md mx-auto bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">User Profile</h2>
      {user ? (
        <div className="space-y-4">
          <div className="bg-gray-50 p-4 rounded-lg">
            <div className="space-y-3">
              <div>
                <label className="block text-sm font-medium text-gray-600">Full Name</label>
                <p className="text-gray-800">{user.fullname}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-600">Email</label>
                <p className="text-gray-800">{user.email}</p>
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-600">Phone</label>
                <p className="text-gray-800">{user.phone}</p>
              </div>
            </div>
          </div>
          
          <div className="flex flex-col space-y-2">
            <button 
              onClick={() => handleUpdateClick(user.id)}
              className="w-full bg-blue-500 text-white py-2 px-4 rounded-lg hover:bg-blue-600 transition duration-200"
            >
              Update Profile
            </button>
            <button 
              onClick={handleLogout}
              className="w-full bg-gray-500 text-white py-2 px-4 rounded-lg hover:bg-gray-600 transition duration-200"
            >
              Logout
            </button>
          </div>
        </div>
      ) : (
        <p className="text-center text-gray-600">No user data available.</p>
      )}
    </div>
  );
}

export default UserProfile;
