import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      setError('No user ID found. Please log in.');
      setLoading(false);
      return;
    }

    Axios.get(`http://localhost:8080/user/${userId}`)
      .then((response) => {
        setUser(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to fetch user data');
        setLoading(false);
      });
  }, []);

  const updateNavigate = (id) => {
    window.location.href = `/UpdateProfile/${id}`;
  };

  const deleteAccount = async () => {
    const confirmDelete = window.confirm(
      'Are you sure you want to delete your account?'
    );
    if (confirmDelete && user?.id) {
      try {
        await Axios.delete(`http://localhost:8080/user/${user.id}`);
        alert('Account deleted successfully');
        localStorage.removeItem('userId');
        window.location.href = '/Register';
      } catch (error) {
        console.error('Error deleting account:', error);
        alert('Failed to delete account');
      }
    }
  };

  if (loading) return <p className="text-gray-600">Loading...</p>;
  if (error) return <p className="text-red-500 font-semibold">{error}</p>;

  return (
    <div className="p-6 max-w-md mx-auto bg-white rounded-lg shadow-md border border-gray-200">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">User Profile</h2>
      {user ? (
        <div className="space-y-3 text-gray-700">
          <p><strong>Full Name:</strong> {user.fullname}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Phone:</strong> {user.phone}</p>
          <p><strong>Password:</strong> {user.password}</p>

          <div className="flex gap-3 pt-4">
            <button
              onClick={() => updateNavigate(user.id)}
              className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded shadow"
            >
              Update
            </button>
            <button
              onClick={deleteAccount}
              className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded shadow"
            >
              Delete Account
            </button>
          </div>
        </div>
      ) : (
        <p className="text-gray-500">No user data available.</p>
      )}
    </div>
  );
}

export default UserProfile;
