import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';
import './DisplayItem.css'; // Import a CSS file for styling

function DisplayItem() {
  const [InventoryItem, setInventory] = useState([]);
 
  useEffect(() => {
    loadInventory();
  }, [])

  const loadInventory = async () => {
    try {
      const result = await axios.get("http://localhost:8080/inventory");
      setInventory(result.data);
    } catch (error) {
      console.error("Error loading inventory:", error);
    }
  }

  return (
    <div className="inventory-container">
      <h1>Inventory Item</h1>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Item ID</th>
            <th>Item</th>
            <th>Item Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Details</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {InventoryItem.map((item, index) => {
            return (
              <tr key={index}>
                <td>{item.itemId}</td>
                <td className="image-cell">
                  <img 
                    src={`http://localhost:8080/uploads/${item.itemImage}`} 
                    alt={item.itemName}
                    className="inventory-image"
                  />
                </td>
                <td>{item.itemName}</td>
                <td>{item.category}</td>
                <td>{item.quantity}</td>
                <td>{item.details}</td>
                <td>
                  <button 
                    onClick={() => (window.location.href=`/edit/${item.itemId}`)} 
                    className="btn btn-primary"
                  >
                    Edit
                  </button>
                </td>
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )
}

export default DisplayItem
