import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function AddItem() {
  const navigate = useNavigate();
  const [Inventory, setInventory] = useState({
    itemId: '',
    itemName: '',
    itemCategory: '',
    itemQty: '',
    itemDetails: '',
    itemImage: '',
  }); 

  const { itemId, itemName, itemCategory, itemQty, itemDetails } = Inventory;

  const onInputChange = (e) => {
    if (e.target.name === "itemImage") {
      setInventory({ ...Inventory, [e.target.name]: e.target.files[0] });
    } else {
      setInventory({ ...Inventory, [e.target.name]: e.target.value }); 
    }
  };

  const onsubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", Inventory.itemImage);
    let imageName = "";

    try {
      const response = await axios.post("http://localhost:8080/inventory/itemImg", formData, {
        headers: { 
          'Content-Type': 'multipart/form-data' },
      });
      imageName = response.data.fileName;
    } catch (error) {
      alert("Error uploading image. Please try again.");
      return;
    }

    const updatedInventory = { ...Inventory, itemImage: imageName };
    await axios.post("http://localhost:8080/inventory", updatedInventory);
    alert("Item added successfully!");
    window.location.reload();
  }

  return (
    <div className="min-h-screen bg-green-50 py-8 px-4">
      <div className="max-w-2xl mx-auto">
        <h1 className="text-3xl font-bold text-green-800 text-center mb-8">Add Item</h1>
        
        <div className="bg-white shadow-lg rounded-lg p-8 border-2 border-green-100">
          <form id="itemForm" onSubmit={(e) => onsubmit(e)} className="space-y-6">
            
            <div>
              <label htmlFor="itemId" className="block text-sm font-medium text-green-700 mb-2">
                Item ID
              </label>
              <input 
                type="text" 
                id="itemId" 
                name="itemId" 
                value={itemId} 
                onChange={(e) => onInputChange(e)} 
                required 
                className="w-full px-3 py-2 border border-green-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
              />
            </div>

            <div>
              <label htmlFor="itemName" className="block text-sm font-medium text-green-700 mb-2">
                Item Name
              </label>
              <input 
                type="text" 
                id="itemName" 
                name="itemName" 
                value={itemName} 
                onChange={(e) => onInputChange(e)} 
                required 
                className="w-full px-3 py-2 border border-green-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
              />
            </div>

            <div>
              <label htmlFor="itemCategory" className="block text-sm font-medium text-green-700 mb-2">
                Item Category
              </label>
              <select 
                id="itemCategory" 
                name="itemCategory" 
                value={itemCategory} 
                onChange={(e) => onInputChange(e)} 
                required
                className="w-full px-3 py-2 border border-green-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors bg-white"
              >
                <option value="" disabled className="text-gray-500">Select Item Category</option>
                <option value="Apparel & Fashion">Apparel & Fashion</option>
                <option value="Electronics & Gadgets">Electronics & Gadgets</option>
                <option value="Health & Beauty">Health & Beauty</option>
                <option value="Food & Dining">Food & Dining</option>
                <option value="Home & Furniture">Home & Furniture</option>
              </select>
            </div>

            <div>
              <label htmlFor="itemQty" className="block text-sm font-medium text-green-700 mb-2">
                Item Quantity
              </label>
              <input 
                type="number" 
                id="itemQty" 
                name="itemQty" 
                value={itemQty} 
                onChange={(e) => onInputChange(e)} 
                required 
                min="0"
                className="w-full px-3 py-2 border border-green-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors"
              />
            </div>

            <div>
              <label htmlFor="itemDetails" className="block text-sm font-medium text-green-700 mb-2">
                Item Details
              </label>
              <textarea 
                id="itemDetails" 
                name="itemDetails" 
                rows="4" 
                value={itemDetails} 
                onChange={(e) => onInputChange(e)} 
                required
                className="w-full px-3 py-2 border border-green-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors resize-vertical"
                placeholder="Enter item details..."
              />
            </div>

            <div>
              <label htmlFor="itemImage" className="block text-sm font-medium text-green-700 mb-2">
                Item Image
              </label>
              <input 
                type="file" 
                id="itemImage" 
                name="itemImage" 
                accept="image/*" 
                onChange={(e) => onInputChange(e)}
                className="w-full px-3 py-2 border border-green-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition-colors file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-medium file:bg-green-50 file:text-green-700 hover:file:bg-green-100"
              />
            </div>

            <div className="pt-4">
              <button 
                type="submit" 
                className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-md transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 shadow-sm"
              >
                Add Item
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddItem;
