import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './DisplayItem.css';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';

function DisplayItem() {
  const [inventoryItems, setInventoryItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadInventory();
  }, []);

  const loadInventory = async () => {
    setIsLoading(true);
    try {
      const result = await axios.get("http://localhost:8080/inventory");
      setInventoryItems(result.data);
      setError(null);
    } catch (error) {
      console.error("Error loading inventory:", error);
      setError("Failed to load inventory items. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const updateNavigate = (id) => {
    navigate(`/updateitem/${id}`);
  };

  const deleteItem = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this item?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:8080/inventory/${id}`);
        loadInventory();
        alert("Item deleted successfully.");
      } catch (error) {
        alert("Error deleting item. Please try again later.");
      }
    }
  };

  const generatePDF = () => {
    const doc = new jsPDF("portrait");
    doc.text("Inventory Item List", 14, 10);

    const tableData = inventoryItems.map(item => [
      item.itemId || 'N/A',
      item.itemName || 'N/A',
      item.itemCategory || 'N/A',
      item.itemQty || 'N/A',
      item.itemDetails || 'N/A',
    ]);

    doc.autoTable({
      head: [['Item ID', 'Item Name', 'Category', 'Quantity', 'Details']],
      body: tableData,
      startY: 20,
    });

    doc.save("inventory_items.pdf");
  };

  if (isLoading) return <div className="loading">Loading inventory items...</div>;
  if (error) return <div className="error-message">{error}</div>;
  if (!inventoryItems || inventoryItems.length === 0) return <div className="no-data">No inventory items found.</div>;

  return (
    <div className="inventory-container">
      <h1>Inventory Items</h1>
      <button onClick={generatePDF} className="pdf-button">Generate PDF</button>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Item ID</th>
            <th>Item</th>
            <th>Item Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Details</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {inventoryItems.map((item, index) => (
            <tr key={index}>
              <td>{item.itemId || 'N/A'}</td>
              <td className="image-cell">
                {item.itemImage ? (
                  <img
                    src={`http://localhost:8080/uploads/${item.itemImage}`}
                    alt={item.itemName || 'Item image'}
                    className="inventory-image"
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = 'https://via.placeholder.com/100?text=No+Image';
                    }}
                  />
                ) : (
                  <div className="no-image">No image</div>
                )}
              </td>
              <td>{item.itemName || 'N/A'}</td>
              <td>{item.itemCategory || 'N/A'}</td>
              <td>{item.itemQty || 'N/A'}</td>
              <td>{item.itemDetails || 'N/A'}</td>
              <td>
                <button onClick={() => updateNavigate(item.id)} className="edit-button">Update</button>
                <button onClick={() => deleteItem(item.id)} className="delete-button">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default DisplayItem;
