import React from 'react';

function Home() {
  return (
    <div className="min-h-screen flex items-center justify-center bg-green-50">
      <div className="bg-white p-10 rounded-lg shadow-lg text-center space-y-4 w-full max-w-md border-2 border-green-100">
        <h1 className="text-3xl font-bold mb-6 text-green-800">Welcome</h1>

        <button
          onClick={() => (window.location.href = '/additem')}
          className="w-full bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 transition-colors duration-200 font-medium shadow-sm"
        >
          Add Item
        </button>

        <button
          onClick={() => (window.location.href = '/allitems')}
          className="w-full bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 transition-colors duration-200 font-medium shadow-sm"
        >
          All Items
        </button>

        <button
          onClick={() => (window.location.href = '/Register')}
          className="w-full bg-green-500 text-white py-3 px-4 rounded-lg hover:bg-green-600 transition-colors duration-200 font-medium shadow-sm"
        >
          Register
        </button>

        <button
          onClick={() => (window.location.href = '/Login')}
          className="w-full bg-green-800 text-white py-3 px-4 rounded-lg hover:bg-green-900 transition-colors duration-200 font-medium shadow-sm"
        >
          Login
        </button>
      </div>
    </div>
  );
}

export default Home;
