import React, { useEffect,useState } from 'react'
import  { useParams } from 'react-router-dom'
import axios from 'axios';

function DisplayItem() {
  const [InventoryItem, setInventory] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    loadInventory();
  }, [])

    const loadInventory = async () => {
    const result = await axios.get("http://localhost:8080/resources/inventory/");
    setInventory(result.data);
    }
  return (
    <div>
      <h1>Inventory Item</h1>
      <table>
        <thead>
          <tr>
            <th>Item ID</th>
            <th>Item</th>
            <th>Item Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Details</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {InventoryItem.map((item, index) => {
            return (
              <tr key={index}>
                <td>{item.itemId}</td>
                <td>
                  <img src={`http://localhost:8080/uploads/${item.itemImage}`} 
                  alt={item.itemName} 
                 />
                </td>
                <td>{item.itemName}</td>
                <td>{item.category}</td>
                <td>{item.quantity}</td>
                <td>{item.details}</td>
                <td><button onClick={()=>(window.location.href=`/edit/${item.itemId}`)} className="btn btn-primary">Edit</button></td>
              </tr>
            )
          })}
          </tbody>
      </table>
    </div>
  )
}

export default DisplayItem
