import React from 'react'
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

function Register() {
      const navigate = useNavigate();
      const [user, setUser] = React.useState({
        fullname: '',
        email: '',
        password: '',
        phone: ''
      });
      const { fullname, email, password, phone } = user;

      const onInputChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
      }
      const onSubmit = async (e) => {
        e.preventDefault();
        await Axios.post('http://localhost:3000/user', user);
        alert('User registered successfully');
        window.location.href = '/Login';
      }
  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={(e) => onSubmit(e) }>

        <label for="fullname">fullname</label><br />
        <input type="text" id="fullname" name="fullname" onChange={(e) => onInputChange(e)} value={fullname} required/>
        <br />

        <label for="email">Email</label><br />
        <input type="email" id="email" name="email" email="email" onChange={(e) => onInputChange(e)} value={email} required/>
        <br />

        <label for="password">Password</label><br />
        <input type="password" id="password" name="password" required/>
        <br />   
        <label for="phone">Phone</label><br />
        <input type="tel" id="phone" name="phone" required/>
        <br />
        <button type="submit">Register</button>

      </form>
      </div>
  )
}

export default Register
