import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios';
import './DisplayItem.css'; // Import a CSS file for styling

function DisplayItem() {
  const [InventoryItem, setInventory] = useState([]);
 
  useEffect(() => {
    loadInventory();
  }, [])

  const loadInventory = async () => {
    const result = await axios.get("http://localhost:8080/inventory");
    setInventory(result.data);
  }
// const updateNavigate = (id) => {
//   window.location.href = `/updateitem/${id}`;
  return (
    <div className="inventory-container">
      <h1>Inventory Item</h1>
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Item ID</th>
            <th>Item</th>
            <th>Item Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Details</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {InventoryItem.map((item, index) => {
            return (
              <tr key={index}>
                <td>{item.itemId}</td>
                <td className="image-cell">
                  <img 
                    src={`http://localhost:8080/uploads/${item.itemImage}`} 
                    alt={item.itemName}
                    className="inventory-image"
                  />
                </td>
                <td>{item.itemName}</td>
                <td>{item.category}</td>
                <td>{item.quantity}</td>
                <td>{item.details}</td>
                {/* <td>
                 <button onClick={() => updateNavigate(item.id)} className="edit-button">Update</button>
                </td> */}
              </tr>
            )
          })}
        </tbody>
      </table>
    </div>
  )

}
export default DisplayItem;
