import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import "./UpdateItem.css"; // Create this CSS file for styling

function UpdateItem() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [itemData, setItemData] = useState({
    itemId: "",
    itemName: "",
    itemCategory: "",
    itemQty: "",
    itemDetails: "",
    itemImage: ""
  });
  const [previewImage, setPreviewImage] = useState(null);
  const [file, setFile] = useState(null);

  // Fetch item data on component mount
  useEffect(() => {
    const fetchItemData = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`http://localhost:8080/inventory/${id}`);
        setItemData(response.data);
        setPreviewImage(`http://localhost:8080/uploads/${response.data.itemImage}`);
      } catch (err) {
        console.error("Error fetching item data:", err);
        setError("Failed to load item data. Please try again.");
      } finally {
        setIsLoading(false);
      }
    };

    fetchItemData();
  }, [id]);

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setItemData({
      ...itemData,
      [name]: value
    });
  };

  // Handle image file selection
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      
      // Create a preview of the selected image
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target.result);
      };
      reader.readAsDataURL(selectedFile);
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      // Create form data to handle file upload
      const formData = new FormData();
      formData.append("itemId", itemData.itemId);
      formData.append("itemName", itemData.itemName);
      formData.append("itemCategory", itemData.itemCategory);
      formData.append("itemQty", itemData.itemQty);
      formData.append("itemDetails", itemData.itemDetails);
      
      // Only append file if a new one was selected
      if (file) {
        formData.append("itemImage", file);
      } else {
        formData.append("itemImage", itemData.itemImage);
      }

      // Send update request to API
      await axios.put(`http://localhost:8080/inventory/${id}`, formData, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      });

      // Navigate back to inventory list on success
      navigate("/allitems");
    } catch (err) {
      console.error("Error updating item:", err);
      setError("Failed to update item. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  // Navigate back to inventory list
  const handleCancel = () => {
    navigate("/allitems");
  };

  if (isLoading && !itemData.itemId) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading item data...</p>
      </div>
    );
  }

  return (
    <div className="update-item-container">
      <h1>Update Item</h1>
      
      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError(null)}>Dismiss</button>
        </div>
      )}

      <form onSubmit={handleSubmit} className="update-form">
        <div className="form-group">
          <label htmlFor="itemId">Item ID:</label>
          <input
            type="text"
            id="itemId"
            name="itemId"
            value={itemData.itemId}
            onChange={handleInputChange}
            required
            readOnly // Generally, IDs shouldn't be changed
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemName">Item Name:</label>
          <input
            type="text"
            id="itemName"
            name="itemName"
            value={itemData.itemName}
            onChange={handleInputChange}
            required
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemCategory">Category:</label>
          <input
            type="text"
            id="itemCategory"
            name="itemCategory"
            value={itemData.itemCategory}
            onChange={handleInputChange}
            required
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemQty">Quantity:</label>
          <input
            type="number"
            id="itemQty"
            name="itemQty"
            value={itemData.itemQty}
            onChange={handleInputChange}
            required
            min="0"
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label htmlFor="itemDetails">Details:</label>
          <textarea
            id="itemDetails"
            name="itemDetails"
            value={itemData.itemDetails}
            onChange={handleInputChange}
            rows="4"
            className="form-control"
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="itemImage">Image:</label>
          <input
            type="file"
            id="itemImage"
            name="itemImage"
            onChange={handleFileChange}
            accept="image/*"
            className="form-control"
          />
          {previewImage && (
            <div className="image-preview">
              <img
                src={previewImage}
                alt="Item preview"
                className="preview-img"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = 'https://via.placeholder.com/150?text=No+Image';
                }}
              />
            </div>
          )}
        </div>

        <div className="form-actions">
          <button
            type="button"
            onClick={handleCancel}
            className="btn cancel-btn"
            disabled={isLoading}
          >
            Cancel
          </button>
          <button
            type="submit"
            className="btn update-btn"
            disabled={isLoading}
          >
            {isLoading ? "Updating..." : "Update Item"}
          </button>
        </div>
      </form>
    </div>
  );
}

export default UpdateItem;
