import  { useParams } from 'react-router-dom'
import axios from 'axios';

function DisplayItem() {
  const [InventoryItem, setInventory] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    loadInventory();
  }, [])

    const loadInventory = async () => {
    const result = await fetch(`http://localhost:8080/inventory/`);
    setInventory( result.json());
    }
  return (
    <div>
      <h1>Inventory Item</h1>
      <table>
        <thead>
          <tr>
            <th>Item ID</th>
            <th>Item</th>
            <th>Item Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Details</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>

          </tbody>
      </table>
    </div>
  )
}

export default DisplayItem
