import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jsPDF } from 'jspdf';
import autoTable from 'jspdf-autotable';

function DisplayItem() {
  const [inventoryItems, setInventoryItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    loadInventory();
    // eslint-disable-next-line
  }, []);

  const loadInventory = async () => {
    setIsLoading(true);
    try {
      const result = await axios.get("http://localhost:8080/inventory");
      setInventoryItems(result.data);
      setError(null);
    } catch (error) {
      console.error("Error loading inventory:", error);
      setError("Failed to load inventory items. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const updateNavigate = (id) => {
    navigate(`/updateitem/${id}`);
  };

  const deleteItem = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this item?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:8080/inventory/${id}`);
        loadInventory();
        alert("Item deleted successfully.");
      } catch (error) {
        console.error("Error deleting item:", error);
        alert("Error deleting item. Please try again later.");
      }
    }
  };

  // PDF generation using jspdf-autotable
  const generatePDF = () => {
    try {
      const doc = new jsPDF("portrait");
      doc.setFontSize(16);
      doc.text("Inventory Item List", 14, 15);

      const columns = [
        { header: "ID", dataKey: "itemId" },
        { header: "Name", dataKey: "itemName" },
        { header: "Category", dataKey: "itemCategory" },
        { header: "Quantity", dataKey: "itemQty" },
        { header: "Details", dataKey: "itemDetails" }
      ];

      const rows = inventoryItems.map(item => ({
        itemId: item.itemId || 'N/A',
        itemName: item.itemName || 'N/A',
        itemCategory: item.itemCategory || 'N/A',
        itemQty: item.itemQty || 'N/A',
        itemDetails: item.itemDetails || 'N/A'
      }));

      autoTable(doc, {
        startY: 25,
        head: [columns.map(col => col.header)],
        body: rows.map(row => columns.map(col => row[col.dataKey])),
        styles: { fontSize: 10 },
        headStyles: { fillColor: [41, 128, 185] },
        margin: { left: 14, right: 14 }
      });

      doc.save("inventory_items.pdf");
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating PDF. Please try again.');
    }
  };

  // Filter items based on search query (case-insensitive)
  const filteredItems = inventoryItems.filter(item =>
    String(item.itemId || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
    String(item.itemName || '').toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Loading state
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 text-lg">Loading inventory items...</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center bg-white p-8 rounded-lg shadow-md max-w-md">
          <div className="text-red-500 text-6xl mb-4">‚ö†Ô∏è</div>
          <div className="text-red-600 text-lg mb-4">{error}</div>
          <button 
            onClick={loadInventory} 
            className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md transition-colors"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  // Empty state
  if (!inventoryItems || inventoryItems.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center bg-white p-8 rounded-lg shadow-md max-w-md">
          <div className="text-gray-400 text-6xl mb-4">üì¶</div>
          <div className="text-gray-600 text-lg mb-4">No inventory items found.</div>
          <button 
            onClick={loadInventory} 
            className="bg-green-600 hover:bg-green-700 text-white font-medium py-2 px-4 rounded-md transition-colors"
          >
            Refresh
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8 px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="bg-white rounded-lg shadow-md p-6 mb-6">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
            <h1 className="text-3xl font-bold text-gray-900">Inventory Items</h1>
            
            <div className="flex flex-col sm:flex-row gap-3">
              <button 
                onClick={generatePDF} 
                className="bg-red-600 hover:bg-red-700 text-white font-medium py-2 px-4 rounded-md transition-colors duration-200 flex items-center justify-center gap-2"
              >
                <span>üìÑ</span> Generate PDF
              </button>
              
              <input
                type="text"
                placeholder="Search by ID and name"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors"
              />
              
              <button 
                onClick={loadInventory} 
                className="bg-green-600 hover:bg-green-700 text-white font-medium py-2 px-4 rounded-md transition-colors duration-200 flex items-center justify-center gap-2"
              >
                <span>üîÑ</span> Refresh
              </button>
            </div>
          </div>
        </div>

        {/* Stats */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <span className="text-blue-800 font-medium">
            Total Items: {inventoryItems.length} | Showing: {filteredItems.length}
          </span>
        </div>

        {/* Table */}
        <div className="bg-white rounded-lg shadow-md overflow-hidden">
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Item ID
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Image
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Item Name
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Category
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Quantity
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Details
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredItems.length === 0 ? (
                  <tr>
                    <td colSpan="7" className="px-6 py-12 text-center text-gray-500">
                      <div className="flex flex-col items-center">
                        <div className="text-4xl mb-2">üîç</div>
                        <p>No items match your search.</p>
                      </div>
                    </td>
                  </tr>
                ) : (
                  filteredItems.map((item, index) => (
                    <tr key={item.id || `${item.itemId}-${index}`} className="hover:bg-gray-50 transition-colors">
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {item.itemId || 'N/A'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {item.itemImage ? (
                          <img
                            src={`http://localhost:8080/uploads/${item.itemImage}`}
                            alt={item.itemName || 'Item image'}
                            className="h-16 w-16 object-cover rounded-lg border border-gray-200"
                            loading="lazy"
                            onError={(e) => {
                              e.target.onerror = null;
                              e.target.src = 'https://via.placeholder.com/100x100?text=No+Image';
                            }}
                          />
                        ) : (
                          <div className="h-16 w-16 bg-gray-100 rounded-lg flex items-center justify-center text-gray-400 text-xs border border-gray-200">
                            No image
                          </div>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {item.itemName || 'N/A'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="inline-flex px-2 py-1 text-xs font-semibold rounded-full bg-blue-100 text-blue-800">
                          {item.itemCategory || 'N/A'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        <span className="font-medium">{item.itemQty || 'N/A'}</span>
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-900 max-w-xs">
                        <div className="truncate" title={item.itemDetails || 'N/A'}>
                          {item.itemDetails || 'N/A'}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium space-x-2">
                        <button
                          onClick={() => updateNavigate(item.id)}
                          className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md transition-colors duration-200 text-xs"
                          title="Edit item"
                        >
                          Update
                        </button>
                        <button
                          onClick={() => deleteItem(item.id)}
                          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md transition-colors duration-200 text-xs"
                          title="Delete item"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default DisplayItem;
