import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function UpdateProfile() {
  const {id} = useParams();
  const [formData, setFormData] = useState({
    fullname: '',
    email: '',
    password: '',
    phone: ''
  });
 const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
       const response = await Axios.get(`http://localhost:8080/user/${id}`);
        const itemData = response.data;
        setFormData({
          fullname: itemData.fullname || '',  
          email: itemData.email || '',
          password: itemData.password || '',
          phone: itemData.phone || '',
        });
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  },[id]);
  const onInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...formData,
      [name]: value
    }));
  }
  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      await Axios.put(`http://localhost:8080/user/${id}`, formData);
      alert('User updated successfully');
      navigate('/UserProfile');
    } catch (error) {
      alert('Failed to update user');
    }
  }
  return (
    <div>

 <h1>Update Profile</h1>
      <form onSubmit={onSubmit}>
        <label htmlFor="fullname">Full Name</label><br />
        <input type="text" id="fullname" name="fullname" onChange={onInputChange} value={formData.fullname} required />
        <br />

        <label htmlFor="email">Email</label><br />
        <input type="email" id="email" name="email" onChange={onInputChange} value={formData.email} required />
        <br />

        <label htmlFor="password">Password</label><br />
        <input type="password" id="password" name="password" onChange={onInputChange} value={formData.password} required />
        <br />

        <label htmlFor="phone">Phone</label><br />
        <input type="tel" id="phone" name="phone" onChange={onInputChange} value={formData.phone} required />
        <br />

        <button type="submit">Update</button>
      </form>
    </div>

  )
}

export default UpdateProfile
