import React from 'react';
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

function Register() {
  const navigate = useNavigate();
  const [user, setUser] = React.useState({
    fullname: '',
    email: '',
    password: '',
    phone: ''
  });

  const { fullname, email, password, phone } = user;

  const onInputChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    await Axios.post('http://localhost:8080/user', user);
    alert('User registered successfully');
    navigate('/Login');
  };

  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={onSubmit}>
        <label htmlFor="fullname">Full Name</label><br />
        <input type="text" id="fullname" name="fullname" onChange={onInputChange} value={fullname} required />
        <br />

        <label htmlFor="email">Email</label><br />
        <input type="email" id="email" name="email" onChange={onInputChange} value={email} required />
        <br />

        <label htmlFor="password">Password</label><br />
        <input type="password" id="password" name="password" onChange={onInputChange} value={password} required />
        <br />

        <label htmlFor="phone">Phone</label><br />
        <input type="tel" id="phone" name="phone" onChange={onInputChange} value={phone} required />
        <br />

        <button type="submit">Register</button>
      </form>
    </div>
  );
}

export default Register;
