import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './DisplayItem.css';
import { jsPDF } from 'jspdf';

function DisplayItem() {
  const [inventoryItems, setInventoryItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
 
  useEffect(() => {
    loadInventory();
  }, []);

  const loadInventory = async () => {
    setIsLoading(true);
    try {
      const result = await axios.get("http://localhost:8080/inventory");
      setInventoryItems(result.data);
      setError(null);
    } catch (error) {
      console.error("Error loading inventory:", error);
      setError("Failed to load inventory items. Please try again later.");
    } finally {
      setIsLoading(false);
    }
  };

  const updateNavigate = (id) => {
    navigate(`/updateitem/${id}`);
  };

  // Fixed typo: delteItem -> deleteItem
  const deleteItem = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this item?");
    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:8080/inventory/${id}`);
        loadInventory();
        alert("Item deleted successfully.");
      } catch (error) {
        console.error("Error deleting item:", error);
        alert("Error deleting item. Please try again later.");
      }
    }
  };

  // Simple PDF generation without autoTable
  const generatePDF = () => {
    try {
      const doc = new jsPDF("portrait");
      
      // Title
      doc.setFontSize(16);
      doc.text("Inventory Item List", 14, 15);
      
      // Headers
      doc.setFontSize(10);
      doc.setFont(undefined, 'bold');
      let yPosition = 30;
      doc.text("ID", 14, yPosition);
      doc.text("Name", 35, yPosition);
      doc.text("Category", 80, yPosition);
      doc.text("Quantity", 120, yPosition);
      doc.text("Details", 155, yPosition);
      
      // Draw line under headers
      doc.line(14, yPosition + 2, 195, yPosition + 2);
      
      // Data rows
      doc.setFont(undefined, 'normal');
      yPosition += 10;
      
      inventoryItems.forEach((item, index) => {
        if (yPosition > 270) { // Start new page if needed
          doc.addPage();
          yPosition = 20;
        }
        
        doc.text(String(item.itemId || 'N/A'), 14, yPosition);
        doc.text(String(item.itemName || 'N/A').substring(0, 20), 35, yPosition);
        doc.text(String(item.itemCategory || 'N/A'), 80, yPosition);
        doc.text(String(item.itemQty || 'N/A'), 120, yPosition);
        doc.text(String(item.itemDetails || 'N/A').substring(0, 25), 155, yPosition);
        
        yPosition += 8;
      });

      doc.save("inventory_items.pdf");
    } catch (error) {
      console.error('Error generating PDF:', error);
      alert('Error generating PDF. Please try again.');
    }
  };

  // Single loading/error/empty state handling
  if (isLoading) {
    return <div className="loading">Loading inventory items...</div>;
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="error-message">{error}</div>
        <button onClick={loadInventory} className="retry-button">
          Retry
        </button>
      </div>
    );
  }

  if (!inventoryItems || inventoryItems.length === 0) {
    return (
      <div className="no-data-container">
        <div className="no-data">No inventory items found.</div>
        <button onClick={loadInventory} className="refresh-button">
          Refresh
        </button>
      </div>
    );
  }
  //Search function
const [searchQuery, setSearchQuery] = useState("");
  const filteredItems = inventoryItems.filter(item =>
    item.itemName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    item.itemCategory.toLowerCase().includes(searchQuery.toLowerCase())
  );
  return (
    <div className="inventory-container">
      <div className="inventory-header">
        <h1>Inventory Items</h1>
        <div className="header-actions">
          <button onClick={generatePDF} className="pdf-button">
            Generate PDF
          </button>
          <button onClick={loadInventory} className="refresh-button">
            Refresh
          </button>
        </div>
      </div>
      
      <div className="inventory-stats">
        <span>Total Items: {inventoryItems.length}</span>
      </div>

      <div className="table-container">
        <table className="inventory-table">
          <thead>
            <tr>
              <th>Item ID</th>
              <th>Image</th>
              <th>Item Name</th>
              <th>Category</th>
              <th>Quantity</th>
              <th>Details</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventoryItems.map((item, index) => (
              <tr key={item.id || index}>
                <td>{item.itemId || 'N/A'}</td>
                <td className="image-cell">
                  {item.itemImage ? (
                    <img 
                      src={`http://localhost:8080/uploads/${item.itemImage}`} 
                      alt={item.itemName || 'Item image'}
                      className="inventory-image"
                      loading="lazy"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = 'https://via.placeholder.com/100x100?text=No+Image';
                      }}
                    />
                  ) : (
                    <div className="no-image">No image</div>
                  )}
                </td>
                <td>{item.itemName || 'N/A'}</td>
                <td>{item.itemCategory || 'N/A'}</td>
                <td>{item.itemQty || 'N/A'}</td>
                <td>{item.itemDetails || 'N/A'}</td>
                <td className="actions-cell">
                  <button 
                    onClick={() => updateNavigate(item.id)} 
                    className="edit-button"
                    title="Edit item"
                  >
                    Update
                  </button>
                  <button 
                    onClick={() => deleteItem(item.id)} 
                    className="delete-button"
                    title="Delete item"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default DisplayItem;
