import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function AddItem() {
  const navigate = useNavigate();
  const [Inventory, setInventory] = useState({
    itemId: "",
    itemName: "",
    itemCategory: "",
    itemQty: "",
    itemDetails: "",
    itemImage: null,
  }); 

  const { itemId, itemName, itemCategory, itemQty, itemDetails } = Inventory;

  const onInputChange = (e) => {
    if (e.target.name === "itemImage") {
      setInventory({ ...Inventory, [e.target.name]: e.target.files[0] });
    } else {
      setInventory({ ...Inventory, [e.target.name]: e.target.value });
    }
  };

  const onsubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", Inventory.itemImage);

    let imageName = "";

    try {
      const response = await axios.post("http://localhost:8080/inventory/itemImg", formData, {
        headers: { 
          'Content-Type': 'multipart/form-data' },
      });
      imageName = response.data;
    } catch (error) {
      alert("Error uploading image. Please try again.");
      return;
    }

    const updatedInventory = { ...Inventory, itemImage: imageName };
      await axios.post("http://localhost:8080/inventory", updatedInventory);
      alert("Item added successfully!");
      window.location.reload();
  }

  return (
    <div>
      <p className="auth_topic">Add Item</p>
      <div className="form_container">
        <div className="form_sub_container">
          <form id="itemForm" onSubmit ={(e) => onsubmit(e)}>
            <label htmlFor="itemId">Item ID:</label>
            <br />
            <input type="text" id="itemId" name="itemId" value="itemId"    onChange={(e)=>onInputChange(e)} required />
            <br />

            <label htmlFor="itemName">Item Name:</label>
            <br />
            <input type="text" id="itemName" name="itemName" value={itemName} onChange={(e)=>onInputChange(e)} required />
            <br />

            <label htmlFor="itemCategory">Item Category:</label>
            <br />
            <select id="itemCategory" name="itemCategory" value={itemCategory} onChange={onInputChange} required>
              <option value="" disabled>Select Item Category</option>
              <option value="Apparel & Fashion">Apparel & Fashion</option>
              <option value="Electronics & Gadgets">Electronics & Gadgets</option>
              <option value="Health & Beauty">Health & Beauty</option>
              <option value="Food & Dining">Food & Dining</option>
              <option value="Home & Furniture">Home & Furniture</option>
            </select>
            <br />

            <label htmlFor="itemQty">Item Quantity:</label>
            <br />
            <input type="number" id="itemQty" name="itemQty" value={itemQty} onChange={onInputChange} required />
            <br />

            <label htmlFor="itemDetails">Item Details:</label>
            <br />
            <textarea id="itemDetails" name="itemDetails" rows="4" cols="50" value={itemDetails} onChange={onInputChange} required></textarea>
            <br />

            <label htmlFor="itemImage">Item Image:</label>
            <br />
            <input type="file" id="itemImage" name="itemImage" accept="image/*" onChange={onInputChange} />
            <br />

            <button type="submit" className="form_btn">Submit</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddItem;
